function addWordTooltip(o){o.qtip({content:o.find(".tooltip"),show:{event:"click",solo:!0,effect:function(){$(this).fadeTo(200,1)}},hide:{event:"unfocus",fixed:!0,effect:function(){}},style:{classes:"qtip-bootstrap"},position:{my:"bottom center",at:"top center",viewport:$(window),adjust:{method:"shift flip"}}})}function addWordTooltips(){$(".word.chinese").each(function(){addWordTooltip($(this))})}function addLearningMouseover(o){o.mouseover(function(){$(this).find(".bottom").css("color","#666")}),o.mouseout(function(){$(this).find(".bottom").css("color","#FFFFFF")})}function addLearningMouseovers(){$(".word.learning").each(function(){addLearningMouseover($(this))})}function closeTooltip(){$("div.qtip:visible").hide()}function addCloseWordTooltipActions(){$(".close_tooltip").click(function(){closeTooltip()})}$(document).keyup(function(o){27==o.keyCode?closeTooltip():37==o.keyCode?closeTooltip():39==o.keyCode&&closeTooltip()});var ready;ready=function(){addWordTooltips(),addLearningMouseovers(),addCloseWordTooltipActions()},$(document).ready(ready),$(document).on("page:load",ready);